import React, { useState, useCallback } from 'react';
import axios from 'axios';
import Handsontable from 'handsontable';
import HandsontableReact from '@handsontable/react';
import 'handsontable/dist/handsontable.full.css';
import './ExcelLikeGrid.css';

const ExcelLikeGrid = () => {
  const [data, setData] = useState([
    ['', '', '', '', '', '']
  
  ]);

  // Use useCallback to memoize the callback
  const handleDataChange = useCallback((changes, source) => {
    if (source === 'loadData') return; // Prevent infinite loop
    if (changes) {
      setData(prevData => {
        // Create a copy of the data to update
        const newData = [...prevData]; // Copy previous data
  
        changes.forEach(([rowIndex, propIndex, oldValue, newValue]) => {
          if (newData[rowIndex]) {
            newData[rowIndex][propIndex] = newValue;
          }
        });
  
        return newData;
      });
    }
  }, []);

  const handleSave = async () => {
    try {
      await axios.post('/api/students', { data });
      alert('Data saved successfully!');
    } catch (error) {
      console.error('Error saving data:', error);
      alert('Error saving data');
    }
  };

  return (
    <div className="excel-like-grid">
      <h1>Excel-Like Grid</h1>
      <HandsontableReact
        data={data}
        colHeaders={['name', 'rollNo', 'year', 'hostel', 'slot', 'count']}
        columns={[
          { data: 0 },
          { data: 1 },
          { data: 2 },
          { data: 3 },
          { data: 4 },
          { data: 5 },
        ]}
        rowHeaders
        width="100%"
        height="400"
        licenseKey="non-commercial-and-evaluation" // Add your license key here
        afterChange={handleDataChange}
      />
      <button onClick={handleSave}>Save Data</button>
    </div>
  );
};

export default ExcelLikeGrid;
